mapper.select:
select a.id, a.title, a.image, a.create_time, a.sort, a.type, a.url, a.is_show, (select count(1) from re_advertisement_goods as r where r.advertisement_id = a.id) goods_num from advertisement as a where a.is_delete = 0 and a.type = #{pageObject.type}
and a.title LIKE CONCAT ('%',#{pageObject.title},'%')
order by a.sort limit #{pageObject.startIndex}, #{pageObject.pageSize}

mapper.insert:
insert into advertisement (id, title, image, 
  create_time, sort, type, 
  url, is_show, is_delete
  )
values (#{id,jdbcType=INTEGER}, #{title,jdbcType=VARCHAR}, #{image,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=TIMESTAMP}, #{sort,jdbcType=INTEGER}, #{type,jdbcType=SMALLINT}, 
  #{url,jdbcType=VARCHAR}, #{isShow,jdbcType=SMALLINT}, #{isDelete,jdbcType=SMALLINT}
  )
