mapper.update:
update income
set
  income_all = income_all + #{incomeAll,jdbcType=DOUBLE},
  remain = remain + #{remain,jdbcType=DOUBLE}
where open_id = #{openId}

update income
set
  remain = remain - #{remain},
  wait_cash = 0,
  already_cash = already_cash + #{alreadyCash}
where open_id = #{openId}
