mapper.select:
select l.id, l.open_id, l.leader_wechat, l.leader_name, l.leader_phone, l.leader_ID, l.leader_company_name, l.leader_post, l.leader_address, l.company_id, l.explains, i.income_all as leader_balance, l.introduce_name, l.introduce_phone, l.is_code, l.in_type, l.is_delete, l.is_introduce, l.is_disable, u.nick_name, u.icon from leader as l left join user_base as u on u.open_id = l.open_id left join income as i on i.open_id = l.open_id where 0 = 0 and l.is_delete = 0
and l.leader_company_name LIKE CONCAT ('%',#{pageObject.title},'%')
and l.leader_name LIKE CONCAT ('%',#{pageObject.name},'%')
and l.in_type = #{pageObject.type}
and l.in_type = 0
and l.company_id = #{pageObject.id}
order by
i.income_all desc,
(SELECT sum(t.cout) as consumeSum FROM (select count(1) as cout from product_order as p left join `share` as s on s.open_id = p.open_id where p.one_open_id = l.open_id and p.order_status = 1 ) as t) desc,
l.in_type,l.id desc limit #{pageObject.startIndex}, #{pageObject.pageSize}

select count(1) from leader as l where 0 = 0 and l.is_delete = 0
and l.leader_company_name LIKE CONCAT ('%',#{pageObject.title},'%')
and l.leader_name LIKE CONCAT ('%',#{pageObject.name},'%')
and l.in_type = #{pageObject.type}
and l.in_type = 0
and l.company_id = #{pageObject.id}

mapper.update:
update leader
set
  is_disable = #{status}
where open_id = #{openId}
